def search4vowels():    #定义函数
    '''输出输入字母中的元音字母'''
    vowels = set('aeiouAEIOU')
    word = input("Provide a word to search for vowels:")
    found = vowels.intersection(set(word))
    for vowel in found:
        print(str(vowel))
search4vowels()
